    private void limparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_limparActionPerformed
        txtResultado.setText(" ");
        calc = "zero";
        num1 = 0;
        num2 = 0;
    }//GEN-LAST:event_limparActionPerformed

    private void raizActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_raizActionPerformed
        double num1 = Double.valueOf(txtResultado.getText());
        double resultado = Math.sqrt((double) (num1));
        String resultadoString = String.valueOf(resultado);
        txtResultado.setText(resultadoString);

    }//GEN-LAST:event_raizActionPerformed

    private void potenciaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_potenciaActionPerformed
        Float num1 = Float.valueOf(txtResultado.getText());
        resultado = num1 * num1;
        String resultadoString = String.valueOf(resultado);
        txtResultado.setText(resultadoString);
    }//GEN-LAST:event_potenciaActionPerformed

    private void divisaoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_divisaoActionPerformed
        Float num1 = Float.valueOf(txtResultado.getText());
        calc = "dividir";
        num2 = num1;
        txtResultado.setText("");
    }//GEN-LAST:event_divisaoActionPerformed

