    private void apagarUmActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_apagarUmActionPerformed
        String tira = txtResultado.getText();
        tira = tira.substring(0, tira.length() - 1);
        txtResultado.setText(tira);
    }//GEN-LAST:event_apagarUmActionPerformed

    private void igualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_igualActionPerformed
        if (calc.equals("adicao")) {
            Float num1 = Float.valueOf(txtResultado.getText());
            resultado = num2 + num1;
            String resultadoString = String.valueOf(resultado);
            txtResultado.setText(resultadoString);
        } else if (calc.equals("subtrair")) {
            Float num1 = Float.valueOf(txtResultado.getText());
            resultado = num2 - num1;
            String resultadoString = String.valueOf(resultado);
            txtResultado.setText(resultadoString);
        } else if (calc.equals("multiplicar")) {
            Float num1 = Float.valueOf(txtResultado.getText());
            resultado = num2 * num1;
            String resultadoString = String.valueOf(resultado);
            txtResultado.setText(resultadoString);
        } else if (calc.equals("dividir")) {
            Float num1 = Float.valueOf(txtResultado.getText());
            resultado = num2 / num1;
            String resultadoString = String.valueOf(resultado);
            txtResultado.setText(resultadoString);
        }
    }//GEN-LAST:event_igualActionPerformed

